SELECT min(k.keyword) AS movie_keyword, min(n.name) AS actor_name, min(t.title) AS hero_movie
FROM cast_info AS ci, keyword AS k, movie_keyword AS mk, name AS n, title AS t
WHERE k.id = mk.keyword_id AND t.id = mk.movie_id AND t.id = ci.movie_id AND ci.movie_id = mk.movie_id AND n.id = ci.person_id
AND t.kind_id > 0 AND k.keyword IN ('bowling-ball-dropped-on-toe', 'chorister', 'munchausen-by-proxy', 'raft-of-corpses', 'reference-to-james-earl-jones', 'reference-to-peter-max', 'secret-paper', 'the-republic-of-baseball') AND ci.person_role_id > 1665416 AND n.md5sum < '352320171abf9a1e934383dfd22827ab';