SELECT min(n.name) AS member_in_charnamed_movie
FROM cast_info AS ci, company_name AS cn, keyword AS k, movie_companies AS mc, movie_keyword AS mk, name AS n, title AS t
WHERE n.id = ci.person_id AND ci.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_id = cn.id AND ci.movie_id = mc.movie_id AND ci.movie_id = mk.movie_id AND mc.movie_id = mk.movie_id
AND mk.keyword_id > 129031 AND k.keyword < 'charlie-chaplin-impersonator' AND cn.country_code < '[am]' AND t.phonetic_code IS NOT NULL AND ci.role_id < 2 AND t.production_year IN (1912, 1921, 1940, 2005) AND mc.company_id < 226492;