SELECT min(t.title) AS movie_title
FROM company_name AS cn, keyword AS k, movie_companies AS mc, movie_keyword AS mk, title AS t
WHERE cn.id = mc.company_id AND mc.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND mc.movie_id = mk.movie_id
AND cn.id < 151074 AND mc.note IN ('(1981) (East Germany) (TV) (DFF1)', '(1986) (Norway) (all media)', '(1998) (non-USA) (all media) (TV) (video)', '(2006-2007) (non-USA) (TV)', '(2008) (Germany) (TV) (uncut version)', '(2008) (USA) (DVD) (as Eastern Star)') AND cn.name_pcode_nf < 'L1323' AND t.production_year > 1907 AND mk.keyword_id < 68423;