SELECT min(mi.info) AS budget, min(t.title) AS unsuccsessful_movie
FROM company_name AS cn, company_type AS ct, info_type AS it1, info_type AS it2, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, title AS t
WHERE t.id = mi.movie_id AND t.id = mi_idx.movie_id AND mi.info_type_id = it1.id AND mi_idx.info_type_id = it2.id AND t.id = mc.movie_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id AND mc.movie_id = mi.movie_id AND mc.movie_id = mi_idx.movie_id AND mi.movie_id = mi_idx.movie_id
AND mi.info IN ('$8,763,762 (USA) (7 January 2001)', '$957,406 (USA) (22 June 2003)', 'Rebichon, Michel. In: "Studio" (France), October 1989, Pg. 21, (MG)', 'Sweden:12 July 1905', 'The original air date of April 15, 1971 is for the Eastern and Central time zones only. The show led into an NBA basketball playoff game, which ran long and pre-empted the show in the Mountain and Pacific time zones (where it was to run after the game). Viewers in that time zone saw this show on April 29.', 'USA:10 July 1915', 'Wilbur Pope lived at 1046 Hollybrook Lane, Encino, California in this original pilot.') AND cn.country_code LIKE '%]%' AND mi_idx.info_type_id < 100 AND ct.kind IN ('distributors', 'miscellaneous companies', 'production companies', 'special effects companies') AND t.season_nr IS NOT NULL;