SELECT min(t.title) AS american_vhs_movie
FROM company_type AS ct, info_type AS it, movie_companies AS mc, movie_info AS mi, title AS t
WHERE t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id
AND ct.kind LIKE '%di%' AND mc.note IN ('(1948) (India) (theatrical) (Bengal)', '(1955) (Brazil) (theatrical)', '(1957) (Netherlands) (TV)', '(1993) (UK) (video)', '(199?) (Hungary) (VHS)', '(2004) (Brazil) (DVD)', '(2005) (Belgium) (all media) (Benelux)', '(2009) (Cyprus) (all media)', '(2012) (Japan) (video) (VOD)', '(presents) (as N.F.D.C.)') AND mi.note > '(Spill Dot Con)';