SELECT min(cn.name) AS movie_company, min(mi_idx.info) AS rating, min(t.title) AS western_violent_movie
FROM company_name AS cn, company_type AS ct, info_type AS it1, info_type AS it2, keyword AS k, kind_type AS kt, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, movie_keyword AS mk, title AS t
WHERE kt.id = t.kind_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND t.id = mi_idx.movie_id AND t.id = mc.movie_id AND mk.movie_id = mi.movie_id AND mk.movie_id = mi_idx.movie_id AND mk.movie_id = mc.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mc.movie_id AND mc.movie_id = mi_idx.movie_id AND k.id = mk.keyword_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id
AND mi.info IN ('Basque Country, Spain', 'Briggs, Nick. In: "Film Review" (UK), September 1998, Pg. 20, (MG)', 'Monksland, Athlone, County Westmeath, Ireland', 'Official selection Budapest International Short Film Festival 2007', 'Prashant Bhilare / Prashant Bhilare Picture', 'The gang again split into two teams to tackle two more cases. A very sensitive case of a murdered teenager at a prestigious school involves Cal and Torres investigating a judge appointed by the President. In the other case Dr. Foster and Loker investigate a NASA test pilot who appeared to black out and ejected seconds before he crashed his top secret SCRAM jet into the ground.', 'When Randy is being taken away by the Pueblo police the car door reads, "To patronize and annoy".') AND t.episode_of_id > 207059;