SELECT min(k.keyword) AS movie_keyword, min(n.name) AS actor_name, min(t.title) AS hero_movie
FROM cast_info AS ci, keyword AS k, movie_keyword AS mk, name AS n, title AS t
WHERE k.id = mk.keyword_id AND t.id = mk.movie_id AND t.id = ci.movie_id AND ci.movie_id = mk.movie_id AND n.id = ci.person_id
AND ci.person_id < 2207777 AND t.phonetic_code > 'H6365' AND t.kind_id < 4 AND k.keyword IN ('associates', 'children-in-bed-with-mother', 'health-clinic', 'industrial-age', 'last-rites', 'painting-children-green', 'reference-to-calvin-klein', 'remains', 'wagon-train-scout', 'wmap-image') AND n.id < 3840838 AND n.surname_pcode IS NOT NULL;