SELECT min(an.name) AS cool_actor_pseudonym, min(t.title) AS series_named_after_char
FROM aka_name AS an, cast_info AS ci, company_name AS cn, keyword AS k, movie_companies AS mc, movie_keyword AS mk, name AS n, title AS t
WHERE an.person_id = n.id AND n.id = ci.person_id AND ci.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_id = cn.id AND an.person_id = ci.person_id AND ci.movie_id = mc.movie_id AND ci.movie_id = mk.movie_id AND mc.movie_id = mk.movie_id
AND an.surname_pcode IS NOT NULL AND t.episode_nr < 3247 AND cn.md5sum > '162651e7875c8b6372ea07b86cb32342' AND n.md5sum IS NOT NULL AND an.md5sum IN ('13ac9edc31847e8b54c854acd5ca1898', '3d0e4bae92dd3fefe4b1c136dba7f6cd', '54dc6c6cf1c7e5d15b184cecc92fe04d', '5a2155009773496616c17b5b46c74891', '6bd908e6e5c2603772c91a67583bd863', '904cd74e5532669761bdd5140d97cad0', '93ecb6bd1a60090706c90c87fdc04f31', 'c0206a40139051f84225f413b548d95f', 'f6a7beddd6d2f27897bf70036fec8432') AND t.phonetic_code > 'O1312' AND ci.role_id > 2;