SELECT min(k.keyword) AS movie_keyword, min(n.name) AS actor_name, min(t.title) AS hero_movie
FROM cast_info AS ci, keyword AS k, movie_keyword AS mk, name AS n, title AS t
WHERE k.id = mk.keyword_id AND t.id = mk.movie_id AND t.id = ci.movie_id AND ci.movie_id = mk.movie_id AND n.id = ci.person_id
AND t.title IN ('Big Poison', 'Komamonoya Hikobei', 'Love and the Confession/Love and the Disappearing Box/Love and the Hip Arrangement/Love and the Old Flames', 'Ohé! Ohé!', 'Os Cinco Valentes', 'Senpai wa sugokatta', 'The Cute But Not So Fuzzy Four', 'The Grand National Steeplechase: Horses Clearing the Water Jump', 'Yamato 2520');