SELECT min(mi.info) AS budget, min(t.title) AS unsuccsessful_movie
FROM company_name AS cn, company_type AS ct, info_type AS it1, info_type AS it2, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, title AS t
WHERE t.id = mi.movie_id AND t.id = mi_idx.movie_id AND mi.info_type_id = it1.id AND mi_idx.info_type_id = it2.id AND t.id = mc.movie_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id AND mc.movie_id = mi.movie_id AND mc.movie_id = mi_idx.movie_id AND mi.movie_id = mi_idx.movie_id
AND mi.info IN ('"Spartacus der Rebell von Rom". In: "Illustrierter Film-Kurier" (Vienna, Austria), N. Freund - Metropol-Verlag, Iss. 1939, September 1954, Pg. 4', '$14,723,932 (USA) (4 July 1999)', 'Australia:27 October 2006', 'During this movie a Nazi propaganda film is shown, saying that Poland had attacked Germany and started the war. The truth is that the Germans staged a "Polish attack" by dressing Polish prisoners in military uniforms and filming the "invasion" as an excuse to invade Poland.', 'Jackson Hall, Tennessee Performing Arts Center - 505 Deaderick Street, Nashville, Tennessee, USA', 'Malaysia:16 May 2002') AND ct.kind IN ('distributors', 'miscellaneous companies', 'production companies', 'special effects companies') AND cn.name_pcode_sf LIKE '%5%';