SELECT min(mi.info) AS release_date, min(miidx.info) AS rating, min(t.title) AS german_movie
FROM company_name AS cn, company_type AS ct, info_type AS it, info_type AS it2, kind_type AS kt, movie_companies AS mc, movie_info AS mi, movie_info_idx AS miidx, title AS t
WHERE mi.movie_id = t.id AND it2.id = mi.info_type_id AND kt.id = t.kind_id AND mc.movie_id = t.id AND cn.id = mc.company_id AND ct.id = mc.company_type_id AND miidx.movie_id = t.id AND it.id = miidx.info_type_id AND mi.movie_id = miidx.movie_id AND mi.movie_id = mc.movie_id AND miidx.movie_id = mc.movie_id
AND mi.info IN ('Fear comes full circle.', 'Finland:K-12/9', 'Sam Winchester: [after opening the door for Lucifer] Oh my God!::Ruby: Guess again...', 'The film takes us on a journey beyond the boundaries of inter-human relationships where a seven year old boy develops an in-separable bond with a Mango tree. And when this beautiful bond falls upon the way of the daily necessities of the worldly people, it gets torn apart; leaving behind only the small boy alive, and with him, the memories of the past. But this does not stops the kid from growing further. His progressive step towards life becomes the real crux of the whole film.') AND t.production_year > 1893 AND mi.info_type_id < 108 AND kt.id IN (2, 4, 6, 7);